mapper.select:
SELECT  count(*)
FROM member


		WHERE userId = #{ content } 


		WHERE nickName = #{ content }

SELECT  count(*)
FROM member
WHERE nickName = #{ nickName }

mapper.insert:
INSERT INTO memberdevelopskill ( skill ) select skills.* from (
SELECT #{userNo} AS userNo , #{item} AS skill FROM DUAL
) skills

mapper.update:
UPDATE member
SET   status = #{ status }
WHERE userNo = #{ userNo }
